category_features_data.each do |category_key, features|
  category = Category.create!(:key => category_key)
  puts '*'*88
  puts "models.category.data.key.#{category_key}", category_key.titleize
  LocaleApp.sender.post_translation('en', "models.category.data.key.#{category_key}", {}, category_key.titleize)
  features.each do |feature_key|
    category.features.create(:key => feature_key)
    puts "models.feature.data.key#{feature_key}", feature_key.titleize
    LocaleApp.sender.post_translation('en', "models.feature.data.key.#{feature_key}", {}, feature_key.titleize)
  end

